import React, { useState } from 'react';
import { View, Text, ScrollView } from 'react-native';
import HeaderMain from '../../components/headerMain';
import LoginButton from '../../components/mainButton';
import { useNavigation } from '@react-navigation/native';
import { showMessage } from 'react-native-flash-message';
import DropDownComodoInput from '../../components/dropDownComodo';
import styles from './new_componente.styles';
import newComponenteController from './new_componente.controller';

export default function NewComponente() {
    const navigation: any = useNavigation();

    const notify = (message: string) => {
        showMessage({
            message: message,
            type: 'danger',
            icon: 'danger',
        });
    };

    const [TipoValue, setTipoValue] = useState<string | null>(null);
    const [ComodoValue, setComodoValue] = useState<string | null>(null);

    return (
        <ScrollView>
            <View style={styles.container}>
                <HeaderMain text="Novo Comodo" />
                <View style={styles.containerForm}>
                    <View style={styles.formTitle}>
                        <Text style={styles.title}>Crie um Componente</Text>
                        <Text style={styles.subtitle}>
                            Insira os dados para continuar:
                        </Text>
                    </View>
                    <View style={styles.form}>
                        <DropDownComodoInput
                            onChange={(value) => setComodoValue(value)}
                        />
                        <LoginButton
                            textBtn="Cadastrar"
                            onPress={async () => {
                                console.log(
                                    'Before navigation:',
                                    ComodoValue,
                                    TipoValue
                                );

                                const success = await newComponenteController(
                                    ImovelValue,
                                    TipoValue,
                                );

                                console.log('After new imovel route:', success);

                                if (success) {
                                    console.log('Imóvel cadastrado com sucesso!');
                                    navigation.navigate('NewComodoSucess')
                                } else {
                                    console.log('Erro ao cadastrar Imóvel!');
                                    notify('Erro ao cadastrar Imóvel!');
                                }
                            }}
                        />
                    </View>
                </View>
            </View>
        </ScrollView>
    );
}
